('1ES', '1 Esdras (3 Ezra)', 'Deuterocanon', 2, 'deuterocanonical', 'Second Temple (2nd c. BCE)', 'Hebrew (probable)', 'Greek (LXX)', 0.82, ARRAY['Codex Vaticanus (4th c.)','Codex Alexandrinus (5th c.)'], ARRAY['Orthodox (appendix)'], NULL, 0, ARRAY[]::TEXT[], 'Variant account of 2 Chronicles 35-36, Ezra, Nehemiah 7-8. Includes Story of the Three Youths.'),
('2ES', '2 Esdras (4 Ezra, Ezra Apocalypse)', 'Deuterocanon', 2, 'deuterocanonical', 'Late Second Temple / Early Christian (c. 100 CE)', 'Hebrew (probable)', 'Latin, Syriac, Ethiopic, Arabic, Armenian, Georgian', 0.75, ARRAY['Latin Vulgate appendix','Syriac, Ethiopic, Arabic translations'], ARRAY['Orthodox (appendix)','Ethiopian'], NULL, 0, ARRAY[]::TEXT[], 'Jewish apocalyptic work (ch. 3-14). Christian additions (ch. 1-2, 15-16). Quoted by early church fathers.'),
('ENO', '1 Enoch (Ethiopic Enoch)', 'Pseudepigrapha / Ethiopian Canon', 2, 'deuterocanonical-ethiopian', 'Second Temple (3rd c. BCE - 1st c. CE, composite)', 'Aramaic/Hebrew', 'Ge''ez (complete), Aramaic (DSS fragments), Greek (fragments)', 0.89, ARRAY['Aramaic: DSS 4Q201-212, 1Q19-20 (Book of Giants)','Greek: fragments (1st-4th c. CE)','Ge''ez: complete (4th-6th c. translation)'], ARRAY['Ethiopian Orthodox'], 'Jude 1:14-15', 0, ARRAY[]::TEXT[], 'Quoted in NT (Jude 14-15). Aramaic DSS fragments confirm antiquity. Central to Ethiopian canon. Five sections: Book of Watchers, Parables, Astronomical, Dream Visions, Epistle.'),
('JUB', 'Jubilees', 'Pseudepigrapha / Ethiopian Canon', 2, 'deuterocanonical-ethiopian', 'Second Temple (2nd c. BCE)', 'Hebrew', 'Ge''ez (complete), Hebrew (DSS fragments), Latin (fragments)', 0.87, ARRAY['Hebrew: DSS 1Q17-18, 2Q19-20, 3Q5, 4Q216-228, 11Q12 (15 manuscripts)','Ge''ez: complete (4th-6th c. translation)','Latin: fragments'], ARRAY['Ethiopian Orthodox'], NULL, 0, ARRAY[]::TEXT[], 'Retelling of Genesis and Exodus 1-12 with calendar emphasis. 15 Hebrew DSS manuscripts confirm importance at Qumran. 364-day solar calendar vs 354-day lunar.'),
('MEQ', 'Meqabyan (Ethiopian Maccabees)', 'Ethiopian Canon', 2, 'deuterocanonical-ethiopian', 'Unknown (possibly 4th-6th c. CE in Ge''ez)', 'Ge''ez (unique to Ethiopia)', 'Ge''ez (unique to Ethiopia)', 0.6, ARRAY['Ethiopian Orthodox manuscripts'], ARRAY['Ethiopian Orthodox'], NULL, 0, ARRAY[]::TEXT[], 'NOT the same as 1-4 Maccabees. Unique to Ethiopian tradition. Three books of wisdom teaching attributed to ''Meqabyan'' (unknown figure). Limited scholarly analysis.'),
('TGO', 'Gospel of Thomas', 'Apocrypha', 3, 'apocryphal-historical-witness', 'Early Christian (mid-2nd c. CE, sayings possibly 1st c.)', 'Greek (probable)', 'Coptic (Nag Hammadi), Greek (Oxyrhynchus fragments)', 0.74, ARRAY['Coptic: Nag Hammadi Codex II,2 (c. 340 CE)','Greek: POxy 1, 654, 655 (fragments, 3rd c.)'], ARRAY[]::TEXT[], NULL, 0, ARRAY[]::TEXT[], '114 sayings of Yahusha. 50% parallel canonical Gospels. Gnostic elements. Valuable for early Yahusha traditions and sayings source research.'),
('GMA', 'Gospel of Mary (Magdalene)', 'Apocrypha', 3, 'apocryphal-historical-witness', 'Early Christian (2nd c. CE)', 'Greek', 'Coptic (fragmentary), Greek (fragments)', 0.68, ARRAY['Coptic: Berlin Gnostic Codex 8502 (5th c.)','Greek: POxy 3525, PRyl 463 (3rd c.)'], ARRAY[]::TEXT[], NULL, 0, ARRAY[]::TEXT[], 'Dialogue between risen Yahusha and disciples, featuring Mary Magdalene. Fragmentary (10 pages missing). Gnostic themes.'),
